package modchu.lib;import java.util.Map;import modchu.lib.characteristic.Modchu_CastHelper;import modchu.lib.characteristic.Modchu_IEntityCapsBase;import modchu.lib.characteristic.Modchu_ModelBase;import modchu.model.ModchuModel_Main;import modchu.model.multimodel.base.MultiModelBaseBiped;/** * ModelCapsの補助関数群 */public class Modchu_EntityCapsHelper {	public static Object getCapsValue(Object model, Object entityCaps, int pIndex, Object ...pArg) {		Object o = entityCaps == null ? null : getCapsValueCaps(entityCaps, pIndex, pArg);		if (o != null) return o;		return getCapsValueModel(model, entityCaps, pIndex, pArg);	}	public static Object getCapsValue(Object model, Modchu_IEntityCapsBase entityCaps, int pIndex, Object ...pArg) {		if (entityCaps != null				&& entityCaps instanceof Modchu_ModelBaseMaster) {			String s = "Modchu_ModelCapsHelper getCapsValueModel entityCaps == Modchu_ModelBaseMaster !! entityCaps="+entityCaps+" model="+model;			Modchu_Debug.mlDebug(s);			throw new RuntimeException(s);		}		Object o = entityCaps == null ? null : entityCaps.getCapsValue(pIndex, pArg);		if (o != null) return o;		return getCapsValueModel(model, entityCaps, pIndex, pArg);	}	public static Object getCapsValue(Object entityCaps, int pIndex, Object ...pArg) {		return getCapsValueCaps(entityCaps, pIndex, pArg);	}	public static Object getCapsValue(Modchu_IEntityCapsBase entityCaps, int pIndex, Object ...pArg) {		return entityCaps == null ? null : entityCaps.getCapsValue(pIndex, pArg);	}	public static int getCapsValueInt(Object model, Object entityCaps, int pIndex, Object ...pArg) {		return Modchu_CastHelper.Int(getCapsValue(model, entityCaps, pIndex, pArg), 0, 2, 1);	}	public static int getCapsValueInt(Object model, Modchu_IEntityCapsBase entityCaps, int pIndex, Object ...pArg) {		return Modchu_CastHelper.Int(getCapsValue(model, entityCaps, pIndex, pArg), 0, 2, 1);	}	public static long getCapsValueLong(Object model, Object entityCaps, int pIndex, Object ...pArg) {		return Modchu_CastHelper.Long(getCapsValue(model, entityCaps, pIndex, pArg));	}	public static long getCapsValueLong(Object model, Modchu_IEntityCapsBase entityCaps, int pIndex, Object ...pArg) {		return Modchu_CastHelper.Long(getCapsValue(model, entityCaps, pIndex, pArg));	}	public static byte getCapsValueByte(Object model, Object entityCaps, int pIndex, Object ...pArg) {		return Modchu_CastHelper.Byte(getCapsValue(model, entityCaps, pIndex, pArg), (byte) 0, (byte) 2, (byte) 1);	}	public static byte getCapsValueByte(Object model, Modchu_IEntityCapsBase entityCaps, int pIndex, Object ...pArg) {		return Modchu_CastHelper.Byte(getCapsValue(model, entityCaps, pIndex, pArg), (byte) 0, (byte) 2, (byte) 1);	}	public static float getCapsValueFloat(Object model, Object entityCaps, int pIndex, Object ...pArg) {		return Modchu_CastHelper.Float(getCapsValue(model, entityCaps, pIndex, pArg));	}	public static float getCapsValueFloat(Object model, Modchu_IEntityCapsBase entityCaps, int pIndex, Object ...pArg) {		return Modchu_CastHelper.Float(getCapsValue(model, entityCaps, pIndex, pArg));	}	public static double getCapsValueDouble(Object model, Object entityCaps, int pIndex, Object ...pArg) {		return Modchu_CastHelper.Double(getCapsValue(model, entityCaps, pIndex, pArg));	}	public static double getCapsValueDouble(Object model, Modchu_IEntityCapsBase entityCaps, int pIndex, Object ...pArg) {		return Modchu_CastHelper.Double(getCapsValue(model, entityCaps, pIndex, pArg));	}	public static boolean getCapsValueBoolean(Object model, Object entityCaps, int pIndex, Object ...pArg) {		return Modchu_CastHelper.Boolean(getCapsValue(model, entityCaps, pIndex, pArg));	}	public static boolean getCapsValueBoolean(Object model, Modchu_IEntityCapsBase entityCaps, int pIndex, Object ...pArg) {		return Modchu_CastHelper.Boolean(getCapsValue(model, entityCaps, pIndex, pArg));	}	public static boolean getCapsValueBoolean(Object entityCaps, int pIndex, Object ...pArg) {		return Modchu_CastHelper.Boolean(getCapsValueCaps(entityCaps, pIndex, pArg));	}	private static Object getCapsValueCaps(Object entityCaps, int pIndex, Object ...pArg) {		return entityCaps != null ? Modchu_Reflect.invokeMethod(entityCaps.getClass(), "getCapsValue", new Class[]{ int.class, Object[].class }, entityCaps, new Object[]{ pIndex, pArg }) : null;	}	private static Object getCapsValueModel(Object model, Object entityCaps, int pIndex, Object ...pArg) {		model = getModel(model);		if (model == null) {			String s = "Modchu_ModelCapsHelper getCapsValueModel model == null !!";			Modchu_Debug.mlDebug(s);			throw new NullPointerException(s);		}		return model != null ? Modchu_Reflect.invokeMethod(model.getClass(), "getCapsValue", new Class[]{ Modchu_IEntityCapsBase.class, int.class, Object[].class }, model, new Object[]{ entityCaps, pIndex, pArg }, -1) : null;	}	public static Object getModel(Object model) {		return ModchuModel_Main.getModelMaster(model);	}}