package modchu.lib.forgeonly.characteristic;import modchu.lib.Modchu_Debug;import modchu.lib.Modchu_Packet;import net.minecraft.network.INetworkManager;import net.minecraft.network.packet.Packet250CustomPayload;import cpw.mods.fml.common.Mod;import cpw.mods.fml.common.Mod.EventHandler;import cpw.mods.fml.common.event.FMLPreInitializationEvent;import cpw.mods.fml.common.network.IPacketHandler;import cpw.mods.fml.common.network.NetworkMod;import cpw.mods.fml.common.network.Player;@Mod(modid="ModchuPacket", name="ModchuPacket", version="10")@NetworkMod(		clientSideRequired = true,		serverSideRequired = true,		channels = {"Modchu"},		packetHandler = ModchuPacket.class		)public class ModchuPacket implements IPacketHandler {	public ModchuPacket() {	}	public String getName() {		return "ModchuPacket";	}	public String getVersion() {		return "10";	}	@EventHandler	public void preInit(FMLPreInitializationEvent event) {		Modchu_Debug.lDebug("ModchuPacket preInit");	}	public void onPacketData(INetworkManager manager, Packet250CustomPayload packet, Player player) {		Modchu_Debug.lDebug("ModchuPacket onPacketData");		Modchu_Packet.onPacketData(manager, packet, player);	}}